<?php/** * The template for displaying the footer * * Contains the closing of the #content div and all content after * * @package WordPress * @subpackage Twenty_Sixteen * @since Twenty Sixteen 1.0 */?><?php    $office=get_field('office','options');    $location=get_field('location','options');    $information=get_field('information','options');    $phone_number=get_field('phone_number','options');    $hotline=get_field('hotline','options');    $email=get_field('email','options');    $copy_right=get_field('copy_right','options');    $find=array(' ','(',')','.','-');?><footer id="footer" class="footer">    <div class="content-footer" style="background-image: url('<?php echo THEME_URI;?>/images/Melbourne-city.jpg')">        <div class="container">            <div class="row">                <div class="col-lg-6 col-md-6 col-sm-12">                    <div class="content-footer-item">                        <?php echo acf_render('<h4>','</h4>',$office); ?>                        <?php echo acf_render('<p>','</p>',$location); ?>                    </div>                </div>                <div class="col-lg-6 col-md-6 col-sm-12">                    <div class="content-footer-item lien-he">                        <?php echo acf_render('<h4>','</h4>',$information); ?>                        <ul>                            <?php if($phone_number):?><li>Phone: <a href="tel:+<?php echo str_replace($find,'',$phone_number); ?>"><?php echo $phone_number; ?></a></li><?php endif;?>                            <?php if($hotline):?><li>Hotline: <a href="tel:+<?php echo str_replace($find,'',$hotline); ?>"><?php echo $hotline; ?></a></li><?php endif;?>                            <?php if($email):?><li>Email: <a href="mailto:<?php echo $email; ?>"><?php echo $email; ?></a></li><?php endif;?>                        </ul>                    </div>                </div>            </div>        </div>    </div>    <div class="copy-right">        <div class="container">            <div class="row">                <div class="col-lg-6 col-md-6 col-sm-12">                    <?php echo acf_render('<div class="text">','</div>',$copy_right); ?>                </div>                <div class="col-lg-6 col-md-6 col-sm-12">                    <div class="menu-social">                        <?php                        /*wp_nav_menu( array(                            'theme_location' => 'social_menu',                            'menu_class'     => 'social_menu',                        ) );*/                        ?>                    </div>                </div>            </div>        </div>    </div></footer><?php wp_footer(); ?></body></html><?php    $title_feature=get_field('title_view_feature','options');    $list_feature=get_field('list_feature','options');    if($title_feature||$list_feature):?><div class="popup_footer">    <div id="popup_content_ft" class="popup_content_ft">        <div class="bg-feature">            <span class="close-popup"><img src="<?php echo THEME_URI;?>/images/Close.svg"></span>            <?php echo acf_render('<h3>','</h3>',$title_feature); ?>            <?php if($list_feature): ?>            <div class="content-feature">                <?php foreach ($list_feature as $feature_item): ?>                <div class="item-feature">                    <?php echo acf_render('<h4>','</h4>',$feature_item['feature_item']); ?>                    <div class="description-feature">                        <?php echo $feature_item['description'];?>                    </div>                </div>                <?php endforeach;?>            </div>            <?php endif;?>        </div>    </div>    <div class="bg_popup_ft"></div></div><?php endif;?><script>    document.addEventListener('wpcf7mailsent', function (event) {        if (603 == event.detail.contactFormId || 725 == event.detail.contactFormId) {            window.location = "<?php echo get_page_link(653); ?>";        }    }, false);</script><script>	jQuery(document).ready(function(){		var h = jQuery(window).scrollTop() + 1;		jQuery("html,body").animate({ scrollTop: h })	})</script><script>    /*submit form to thank you*/    document.addEventListener( 'wpcf7mailsent', function( event ) {        if ( 603 == event.detail.contactFormId) {/*603 is id form contact*/            window.location="<?php echo get_page_link(653); ?>";/*653 is id page thank you*/        }    }, false );</script>